mapper.select:

SELECT a.no, a.title, a.contents, a.hit, 
	a.reg_date, a.g_no, a.o_no, a.depth, b.name, a.user_no 
 FROM board a , user b 
 WHERE a.user_no = b.no
	AND a.title like #{keyword } 
 ORDER BY g_no desc, o_no asc, depth asc limit #{startIndex }, #{size }

mapper.update:

	 UPDATE board SET 
		title = #{title }  
		, contents = #{contents }
 	WHERE no = #{no }
